import requests
import json

# Extract the base64 from the user's JSON structure
user_json = """{
  "audio": {
    "mime": "audio/mpeg",
    "data": ""
  }
}"""

# Parse the JSON and extract the base64 data
data = json.loads(user_json)
base64_audio = data["audio"]["data"]

# Add padding if needed (base64 strings must be multiples of 4)
padding_needed = (4 - len(base64_audio) % 4) % 4
if padding_needed:
    base64_audio += "=" * padding_needed
    print(f"Added {padding_needed} padding characters")

print(f"Base64 length: {len(base64_audio)}")
print(f"Testing API...")

payload = {"audio": base64_audio}
response = requests.post("http://localhost:8000/detect", json=payload, timeout=60)

print(f"\nStatus: {response.status_code}")
if response.status_code == 200:
    result = response.json()
    print(f"✅ Result: {result['result']}")
    print(f"✅ Confidence: {result['confidence']:.4f}")
else:
    print(f"❌ Error: {response.text}")
