import requests
import json

# The base64 audio data provided by the user
# Note: The user provided a JSON structure with nested "data" field
base64_audio = ""

print("=" * 60)
print("Testing API with Provided Base64 Audio")
print("=" * 60)

# Test the API
api_url = "http://localhost:8000/detect"

try:
    # Create the payload
    payload = {
        "audio": base64_audio
    }
    
    print(f"\n[1/2] Sending request to {api_url}")
    print(f"Audio data length: {len(base64_audio)} characters")
    
    # Send the request
    response = requests.post(api_url, json=payload, timeout=60)
    
    print(f"\n[2/2] Response received")
    print(f"Status Code: {response.status_code}")
    
    if response.status_code == 200:
        result = response.json()
        print(f"\n✅ SUCCESS!")
        print(f"Result: {result['result']}")
        print(f"Confidence: {result['confidence']:.4f}")
    else:
        print(f"\n❌ ERROR")
        print(f"Response: {response.text}")
        
except Exception as e:
    print(f"\n❌ Request failed: {e}")
    import traceback
    traceback.print_exc()

print("\n" + "=" * 60)
print("Test Complete")
print("=" * 60)
